import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import Home from './pages/Home';
import PoojaForm from './pages/PoojaForm';
import Admin from './Admin';

function Navigation() {
  const location = useLocation();

  // Don't show navigation on home page
  if (location.pathname === '/') {
    return null;
  }

  return (
    <nav className="bg-indigo-600 shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex space-x-8">
            <Link
              to="/"
              className="inline-flex items-center px-1 pt-1 text-sm font-medium text-white hover:text-indigo-200 transition-colors"
            >
              Home
            </Link>
            <Link
              to="/pooja"
              className="inline-flex items-center px-1 pt-1 text-sm font-medium text-white hover:text-indigo-200 transition-colors"
            >
              Pooja Registration
            </Link>
            <Link
              to="/admin"
              className="inline-flex items-center px-1 pt-1 text-sm font-medium text-white hover:text-indigo-200 transition-colors"
            >
              Admin Dashboard
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
}

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <Navigation />

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/pooja" element={<RegistrationForm />} />
          <Route path="/lodge" element={<div className="min-h-screen flex items-center justify-center"><h1 className="text-4xl font-bold text-gray-700">Lodge Booking - Coming Soon</h1></div>} />
          <Route path="/admin" element={<Admin />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
